function getReadableTextColor($backgroundColor)
{
    $R = ($backgroundColor >> 16) & 0x000000ff;
    $G = ($backgroundColor >> 8) & 0x000000ff;
    $B = ($backgroundColor) & 0x000000ff;
    $K = (($R * 0.3) + ($G * 0.59) + ($B * 0.11));
    return ($K > 128) ? 0x000000 : 0xffffff;
}

function getColor()
{
    $color   = rand(0, 16777215);
    $_bg     = base_convert($color, 10, 16);
    $_bg     = '#' . (strlen($_bg) == 6 ? $_bg : (str_pad($_bg, 6, "0")));
    $fgColor = getReadableTextColor($color);
    $_ft     = base_convert($fgColor, 10, 16);
    $_ft     = '#' . (strlen($_ft) == 6 ? $_ft : (str_pad($_ft, 6, "0")));
    return ['bg' => $_bg, 'ft' => $_ft];
}

function trimall($str)
{
    $qian = array(" ", "　", "\t", "\n", "\r");
    return str_replace($qian, '', $str);
}

